words = lambda: input().strip().split()
yn = lambda cond: "YES" if cond else "NO"
prefix_sum = lambda arr: list(itertools.accumulate(arr))
rand = randint(1, 10**9)
xor = lambda x: x ^ rand

def test_cases(custom_cases=0):
    return number() if not custom_cases else custom_cases

def to_min_heap(arr):
    heap.heapify(arr)
    return arr

def to_max_heap(arr):
    heap.heapify(arr := [-x for x in arr])
    return arr

def solve():
    w, h = numbers()
    image = [word() for _ in range(h)]

    # trnaspose
    transposed = [''.join(image[j][i] for j in range(h)) for i in range(w)]
    
    # print(transposed)
    # double 
    doubled = []
    for row in transposed:
        doubled_row = ''.join(char * 2 for char in row)
        doubled.append(doubled_row)

    for doubled_row in doubled:
        print(doubled_row)
        print(doubled_row)
   

