def to_min_heap(arr):
    heap.heapify(arr)
    return arr

def to_max_heap(arr):
    heap.heapify(arr := [-x for x in arr])
    return arr

def bfs(start, n, graph):
    dist = [float('inf')] * (n + 1)
    dist[start] = 0
    q = deque([start])
    while q:
        u = q.popleft()
        for v in graph[u]:
            if dist[v] == float('inf'):
                dist[v] = dist[u] + 1
                q.append(v)
    return dist

def solve():
    n, m, s, t = map(int, input().split())
    graph = [[] for _ in range(n + 1)]
    connected = [[False] * (n + 1) for _ in range(n + 1)]

    for _ in range(m):
        u, v = map(int, input().split())
        graph[u].append(v)
        graph[v].append(u)
        connected[u][v] = True
        connected[v][u] = True

    dist_s = bfs(s, n, graph)
    dist_t = bfs(t, n, graph)
    d = dist_s[t]
    count = 0

    for u in range(1, n + 1):
        for v in range(u + 1, n + 1):

            if not connected[u][v]:

                new_dist1 = dist_s[u] + 1 + dist_t[v]
                new_dist2 = dist_s[v] + 1 + dist_t[u]

                if min(new_dist1, new_dist2) >= d:
                    count += 1

    print(count)


if __name__ == "__main__":
    for _ in range(1):
        solve()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   